sourcesContent:
import vendorModel from "../models/vendor.model";
import userModel from "../models/user.model";
import locationModel from "../models/location.model";

export const vendorControl = async (req, res) => {
    try {
        const { userID } = req.body;
        const userData = await userModel.findOne({ _id: userID });
        const userLoc = await locationModel.findOne({ address: userData.address });
        vendorModel.collection.createIndex({ vendor_location: "2dsphere" });

    
            // Location match
            const userLocation = userLoc.location.coordinates;
            console.log(userLocation);

               vendorModel.find({
                vendor_location:{
                    $near:{
                        $geometry:{
                            type:'Point',
                            coordinates:[parseFloat(userLocation[0]), parseFloat(userLocation[1])]
                        },
                        $maxDistance:1000,
                    }
                }
               }).then((vendors) => {
                res.status.json(vendors);
              })
              .catch((err) => {
                res.status(500).json({ error: err.message });
              });
    
    } catch (error) {
        res.status(500).json({
            message: error.message,
        });
    }

};

